export interface ProxyMiddlewareOptions extends Omit<URL, 'toJSON'> {
    cookieRewrite?: boolean;
    headers?: any;
    method?: any;
    preserveHost: boolean;
    route?: string;
    via: boolean;
    /** @deprecated The path property is a concatenation of the pathname and search components. */
    path: string;
}
